		template <typename T>
		int Vector<T>::uniquify_faster() {
			int i = 0, j = 0;   // 两个计数都指向首元素
			while (++j < _size) {   // j扫描到末尾
				if (_elem[i] != _elem[j]) {_elem[++i] = _elem[j];}  // j指向元素移到i后
			}
			_size = ++i; shrunk();      // 改变_size数值后, 缩容
